import { ApplicationSource } from "aplx-hmw-server";
/**
* include all required keys which needs from client configuration
*/
export interface WebConfig {
    /**
     * AWS Cognito
     */
    AWS?: {
        IAM_ROLE_ARN: string;
        AWS_ACCOUNT_ID: string;
        AWS_Region: string;
        USER_POOL_ID: string;
        COGNITO_IDENTITY_POOL_ID: string;
        CLIENT_ID: string;
        API_VERSION: string;
        TEMPORARY_PASSWORD: string;
    };
    /**
     * Application Source
     */
    applicationSource: ApplicationSource;
    /**
     * If application source is aplx, specify DB url
     */
    DB_URL?: string;
}
