import { UserStatus, IUser } from "../common/interface";

export class AplxAuthMapper {
    /**
     * Will map the response of the user which to be created in aplx database
     * @param params refer IUser
     * @param createdBy user id or unique field to identify which user created the user
     */ 
    createUserRequestMapper(params: IUser, createdBy: string) {
        return {
            password: params.password,
            loginName: params.loginName,
            firstName: params.firstName,
            lastName: params.lastName,
            status: UserStatus.active,
            stores: params.stores,
            groups: params.groups,
            roles: params.roles,
            privileges: params.privileges,
            createdBy: createdBy
        }
    }

    /**
     * 
     */
    loginResponseMapper(data) {
        return {
            message: "Login success",
            token: data.token
        }
    }
}