export interface IUser {
    userId?: number,
    firstName: string,
    lastName: string,
    loginName: string,
    password: string,
    stores?: [],
    groups?: [],
    roles?: [],
    privileges?: [],
    status?: UserStatus,
    modifiedBy?: any,
    createdBy?: any
}

export enum UserStatus {
    pending = 0,
    active = 1,
    banned = 2
}

export interface ILogin {
    email: string,
    password: string
}

export interface ILoginSuccessResponse { 
    auth_token: string,
    message: string,
    userInfo:any,
    settings:any,
    privileges: any,
    roles: any,
    storeInfo: any,
    counterInfo: any
}

export interface ILoginErrorResponse {
    message: string
}